import { useState, useEffect } from "react";
import "./Display.css";

const Display = ({ contract, account }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const getdata = async () => {
    setLoading(true);

    try {
      const addressToQuery = document.querySelector(".address").value || account;
      const dataArray = await contract.display(addressToQuery);

      if (dataArray && dataArray.length > 0) {
        const images = dataArray.map((item, i) => (
          <a href={item} key={i} target="_blank" rel="noopener noreferrer">
            <img
              key={i}
              src={`https://gateway.pinata.cloud/ipfs/${item.substring(6)}`}
              alt="new"
              className="image-list"
            />
          </a>
        ));
        setData(images);
      } else {
        setData([]);
        alert("No files to display");
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      alert("An error occurred while fetching data");
    } finally {
      setLoading(false);
    }
  };

  // Clear data when the account changes
  useEffect(() => {
    setData([]);
  }, [account]);

  return (
    <>
      {loading && <p>Loading...</p>}
      <div className="image-list">{data}</div>
      <input type="text" placeholder="Enter Address" className="address" />
      <button className="center button" onClick={getdata}>
        Get Data
      </button>
    </>
  );
};

export default Display;
